import React, { Component } from 'react';
import axios from "axios";


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tweets: [],
      newText: '',
    };

    this.handleTextChange = this.handleTextChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    this.refreshList();
  }

  refreshList = () => {
    axios
      .get("/api/tweets/")
      .then((res) => this.setState({tweets : res.data}))
      .catch((err) => console.log(err))
  }

  handleTextChange(event) {
    this.setState({newText : event.target.value})
  }

  handleSubmit(event) {
    const csrfToken = Cookies.get('csrftoken');
    const item = {
      post_text: this.state.newText,
      image: null,
      pub_date: '1022-02-02T10:03:00Z',
    }

    axios
      .post("/api/tweets/", item)
      .then((res) => this.refreshList());
  }
  renderTweets = () => {
    const tweetList = this.state.tweets;

    return tweetList.map((item) => (
      <li key = {item.id}
      className = "list-group-item d-flex justify-content-between align-items-center">
        <span>
          {item.post_text}
        </span>
      </li>
    ));
  };


  render() {
    return (
      <main className>
        <h1 className="text-black text-center bg-info my-4">Twitter</h1>
        <div class = "input-group mb-3 col-md-6 col-sm-10 mx-auto">
          <input type="text" class = "form-control form-control-lg flex-nowrap mx-auto" placeholder = "enter tweet here" value = {this.state.newText} onChange = {this.handleTextChange}/>
          <div class = "input-group-append">
            <button class="btn btn-outline-secondary" type="button" id="button-addon2" onClick={this.handleSubmit}>Tweet!</button>
          </div>
        </div>
        <div className="row">
          <div className="col-md-6 col-sm-10 mx-auto p-0">
                {this.renderTweets()}
          </div>
        </div>
      </main>
    );
  }

}

export default App;
