import React, { Component } from 'react';
import './App.css';
import axios from "axios";


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tweets: [],
      newText: '',
      isLoggedIn: false,
      registerEmail: '',
      registerPassword1: '',
      registerPassword2: '',
      registerUsername: '',
      loginEmail: '',
      loginPassword: '',
      loginUsername: '',
      registerUser: false,
    };

    this.handleTextChange = this.handleTextChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleRegEmail = this.handleRegEmail.bind(this);
    this.handleRegPassword1=this.handleRegPassword1.bind(this);
    this.handleRegPassword2 = this.handleRegPassword2.bind(this);
    this.handleRegUsername=this.handleRegUsername.bind(this);
    this.register = this.register.bind(this);
    this.handleLoginEmail = this.handleLoginEmail.bind(this);
    this.handleLoginPassword = this.handleLoginPassword.bind(this);
    this.handleLoginUsername=this.handleLoginUsername.bind(this);
    this.login = this.login.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    this.refreshList();
  }

  refreshList = () => {
    axios
      .get("/api/tweets/")
      .then((res) => this.setState({tweets : res.data}))
      .catch((err) => console.log(err))
  }

  handleTextChange(event) {
    this.setState({newText : event.target.value})
  }

  handleSubmit(event) {
    var currentDate = new Date();
    var date = currentDate.getFullYear()+ '-' + (currentDate.getMonth()+1)+ '-' + currentDate.getDate()+'T' + currentDate.getHours()+":"+currentDate.getMinutes()+":00Z"
    const item = {
      post_text: this.state.newText,
      image: null,
      pub_date: date,
    }

    axios
      .post("/api/tweets/", item)
      .then((res) => this.refreshList());
  }

  handleRegEmail(event) {
    this.setState({registerEmail : event.target.value})
  }

  handleRegPassword1(event) {
    this.setState({registerPassword1: event.target.value})
  }

  handleRegPassword2(event) {
    this.setState({registerPassword2: event.target.value})
  }

  handleRegUsername(event) {
    this.setState({registerUsername: event.target.value})
  }

  handleChange(event) {
    this.setState({registerUser : true})
  }

  register(event) {
    event.preventDefault();

    const user = {
      email: this.state.registerEmail,
      username: this.state.registerUsername,
      password1: this.state.registerPassword1,
      password2: this.state.registerPassword2,
    };

    fetch ('http://127.0.0.1:8000/api/v1/users/auth/register/', {
      method : 'POST',
      headers : {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(user)
    })
      .then(res => res.json())
      .then(data => {
        if (data.key) {
          localStorage.clear();
          localStorage.setItem('token', data.key)
          this.setState({isLoggedIn : true});
        } else {
          this.setState({registerEmail: ''});
          this.setState({registerPassword1: ''});
          this.setState({registerPassword2: ''});
          this.setState({registerUsername: ''});
          localStorage.clear();
        }
        });
  };

  handleLoginEmail(event) {
    this.setState({loginEmail : event.target.value})
  }

  handleLoginPassword(event) {
    this.setState({loginPassword: event.target.value})
  }

  handleLoginUsername(event) {
    this.setState({loginUsername: event.target.value})
  }

  login(event) {
    event.preventDefault();

    const user = {
      email: this.state.loginEmail,
      username: this.state.loginUsername,
      password: this.state.loginPassword,
    };


    fetch ('http://127.0.0.1:8000/api/v1/users/auth/login/', {
      method : 'POST',
      headers : {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(user)
    })
      .then(res => res.json())
      .then(data => {
        if (data.key) {
          localStorage.clear();
          localStorage.setItem('token', data.key);
          console.log(data.key);
          this.setState({isLoggedIn : true});
        } else {
          this.setState({loginEmail: ''});
          this.setState({loginPassword: ''});
          this.setState({loginUsername: ''});
          localStorage.clear();
        }
        });
  };

  renderTweets = () => {
    const tweetList = this.state.tweets;

    return tweetList.map((item) => (
      <li key = {item.id}
      className = "list-group-item d-flex justify-content-between align-items-center">
        <span>
          {item.user} : {item.post_text}
        </span>
      </li>
    ));
  };


  render() {
    return (
      <div>
      { this.state.isLoggedIn && 
      <main className>
        <h1 className="text-black text-center bg-info my-4">Twitter</h1>
        <div class = "input-group mb-3 col-md-6 col-sm-10 mx-auto">
          <input type="text" class = "form-control form-control-lg flex-nowrap mx-auto" placeholder = "enter tweet here" value = {this.state.newText} onChange = {this.handleTextChange}/>
          <div class = "input-group-append">
            <button class="btn btn-outline-secondary" type="button" id="button-addon2" onClick={this.handleSubmit}>Tweet!</button>
          </div>
        </div>
        <div className="row">
          <div className="col-md-6 col-sm-10 mx-auto p-0">
                {this.renderTweets()}
          </div>
        </div>
      </main>
      }
      { !this.state.isLoggedIn && 
      <main className>
        <h1 className="text-black text-center bg-info my-4">Twitter</h1>
        <div class="wrapper fadeInDown">
          <div id = "formContent">
            <form>
              <h2>Sign in! </h2>
              <br/>
              <input type = "text" id = "email" class = "fadeIn second" name = "email" placeholder = "email" value = {this.state.loginEmail} onChange = {this.handleLoginEmail}/>
              <br/>
              <input type = "username" id = "username" class = "fadeIn third" name = "username" placeholder = "username" value = {this.state.loginUsername} onChange = {this.handleLoginUsername}/>
              <br/>
              <input type = "password" id = "password" class = "fadeIn fourth" name = "password" placeholder = "password" value = {this.state.loginPassword} onChange = {this.handleLoginPassword}/>
              <br/>
              <input type = "submit" class = "fadeIn fifth" value = "Log in" onClick={this.login}/>
            </form>
            <div id="formFooter">
              <a class = "underlineHover" href = "#">Forgot Password?</a>
              <br/>
              <button class = "underlineHover" onClick={this.handleChange}>Register?</button>
            </div>
          </div>
        </div>
      </main>


      }
      </div>
    );
  }

}

export default App;
